import React from "react";
import heroBg from "../images/Services/project2.jpg";
import aboutImg from "../images/Services/ourteam.jpg";
import processImg from "../images/Services/project4.jpg";
import testimonialsImg from "../images/Services/project5.jpg";
import "./Home.css";

const homeCards = [
  {
    title: "Building Your Dreams, Brick by Brick",
    img: heroBg,
    desc: "Gajulapalli Infra and Constructions - Trusted for Quality, Timely Delivery, and Innovation.",
    cta: true,
  },
  {
    title: "About Us",
    img: aboutImg,
    desc: "With decades of experience, we deliver residential, commercial, and industrial projects with a focus on quality and customer satisfaction.",
  },
  {
    title: "Our Process",
    img: processImg,
    desc: "Consultation, Planning, Execution, and Delivery. We ensure quality and efficiency at every step.",
  },
  {
    title: "Testimonials",
    img: testimonialsImg,
    desc: "Our clients love our professionalism, reliability, and quality. Read what they have to say about us!",
  },
];

function Home() {
  return (
    <div className="home-root">
      <div className="home-bg" />
      <div className="home-overlay" />
      <div className="home-content">
        {/* Home Section Cards Grid */}
        <div className="home-section-cards-grid">
          {homeCards.map((card, idx) => (
            <div className={`home-square-card ${idx % 2 === 0 ? 'odd' : 'even'}`} key={idx}>
              <img src={card.img} alt={card.title} className="home-square-card-img" />
              <div className="home-square-card-content">
                <div className="home-square-card-title">{card.title}</div>
                <div className="home-square-card-desc">{card.desc}</div>
                {card.cta && (
                  <a href="/contact" className="bg-white text-blue-700 px-6 py-2 rounded font-semibold shadow hover:bg-blue-50 mt-4 inline-block">Get in Touch</a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
