import React, { useRef, useEffect, useState } from "react";
import "./ServiceCard.css";

// Importing images
import ModularKitchen from "../im";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../images/Services/Tvunits.jpg";
import Wardrobe from "../images/Services/wardrobs.jpg";
import OfficeSpace from "../images/Services/officespaces.jpg";
import Bedroom from "../images/Services/bedroom.jpg";
import ExternalArchitecture from "../images/Services/ExternalArc.jpg";

const services = [
  {
    title: "Modular Kitchen",
    image: ModularKitchen,
    description: "Custom-designed modular kitchens with modern fittings and finishes for efficient and stylish cooking spaces.",
  },
  {
    title: "TV Units",
    image: TVUnit,
    description: "Elegant and functional TV units tailored to your living room aesthetics and storage needs.",
  },
  {
    title: "Wardrobes",
    image: Wardrobe,
    description: "Spacious and customizable wardrobes to organize your clothing and accessories beautifully.",
  },
  {
    title: "Office Spaces",
    image: OfficeSpace,
    description: "Modern office interiors designed for productivity, comfort, and a professional look.",
  },
  {
    title: "Bedrooms",
    image: Bedroom,
    description: "Cozy and stylish bedroom designs for restful nights and refreshing mornings.",
  },
  {
    title: "External Architecture",
    image: ExternalArchitecture,
    description: "Striking external architectural solutions to enhance your building's curb appeal and value.",
  },
];

function ServiceRow({ service, index }) {
  const rowRef = useRef(null);
  const [inView, setInView] = useState(false);

  useEffect(() => {
    const observer = new window.IntersectionObserver(
      ([entry]) => {
        setInView(entry.isIntersecting);
      },
      { threshold: 0.2 }
    );
    if (rowRef.current) {
      observer.observe(rowRef.current);
    }
    return () => observer.disconnect();
  }, []);

  return (
    <div
      ref={rowRef}
      className={`service-row ${index % 2 === 1 ? "reverse" : ""} ${inView ? "in-view" : ""}`}
    >
      <div className="service-image-half">
        <img
          src={service.image}
          alt={service.title}
          className="service-image"
        />
      </div>
      <div className="service-content-half">
        <h3 className="service-title">{service.title}</h3>
        <p className="service-description">{service.description}</p>
      </div>
    </div>
  );
}

function Services() {
  return (
    <section className="services-section">
      <div className="services-container">
        <h2 className="services-heading">Our Services</h2>
        <div className="services-list">
          {services.map((service, index) => (
            <ServiceRow key={index} service={service} index={index} />
          ))}
        </div>
      </div>
    </section>
  );
}

export default Services;
