import { useState } from 'react';

const images = [
  '/assets/project1.jpg',
  '/assets/project2.jpg',
  '/assets/project3.jpg',
  '/assets/project4.jpg',
  '/assets/project5.jpg',
  '/assets/project6.jpg',
];

function Projects() {
  const [lightbox, setLightbox] = useState({ open: false, img: '' });

  return (
    <div className="max-w-6xl mx-auto py-16 px-4">
      <h1 className="text-4xl font-bold mb-10 text-center">Our Projects</h1>
      <div className="grid grid-cols-1 sm:grid-cols-2 md:grid-cols-3 gap-8">
        {images.map((img, i) => (
          <div key={i} className="relative group cursor-pointer project-card" onClick={() => setLightbox({ open: true, img })}>
            <img src={img} alt={`Project ${i + 1}`} className="rounded shadow-lg w-full h-56 object-cover" />
            <div className="absolute inset-0 bg-black bg-opacity-30 flex items-center justify-center opacity-0 group-hover:opacity-100 transition">
              <span className="text-white text-lg font-semibold">View</span>
            </div>
          </div>
        ))}
      </div>
      {/* Lightbox Modal */}
      {lightbox.open && (
        <div className="fixed inset-0 bg-black bg-opacity-80 flex items-center justify-center z-50">
          <span className="absolute top-8 right-8 text-white text-3xl cursor-pointer" onClick={() => setLightbox({ open: false, img: '' })}>&times;</span>
          <img src={lightbox.img} alt="Project Preview" className="max-h-[80vh] max-w-[90vw] rounded shadow-lg" />
        </div>
      )}
    </div>
  );
}

export default Projects;
