import React from "react";
import modularkitchen from './'

const services = [
  {
    title: "Modular Kitchen",
    image: "src\images\Services\modularkitchen.jpg"
  },
  {
    title: "False Ceiling",
    image: "/images/services/false-ceiling.jpg",
  },
  {
    title: "TV Units",
    image: "/images/services/tv-unit.jpg",
  },
  {
    title: "Wardrobes",
    image: "/images/services/wardrobe.jpg",
  },
  {
    title: "Office Spaces",
    image: "/images/services/office-space.jpg",
  },
  {
    title: "Bedrooms",
    image: "/images/services/bedroom.jpg",
  },
  {
    title: "External Architecture",
    image: "/images/services/external-architecture.jpg",
  },
];

function Services() {
  return (
    <section className="bg-slate-50 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-blue-700 mb-10">Our Services</h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white shadow-md rounded-xl overflow-hidden hover:shadow-xl transition duration-300 group"
            >
              <img
                src={service.image}
                alt={service.title}
                className="w-full h-52 object-cover group-hover:scale-105 transition-transform duration-300"
              />
              <div className="p-5 text-center">
                <h3 className="text-xl font-semibold text-slate-800">{service.title}</h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Services;
