function TestimonialCard({ name, location, text, image }) {
  return (
    <div className="bg-white rounded-2xl shadow-lg p-8 flex flex-col items-center text-center">
      {image && (
        <img src={image} alt={name} className="w-16 h-16 rounded-full mb-4 object-cover" />
      )}
      <p className="text-lg italic mb-4">“{text}”</p>
      <div className="font-bold text-blue-700">{name}</div>
      <div className="text-sm text-gray-500">{location}</div>
    </div>
  );
}

export default TestimonialCard;
