import React, { useRef, useEffect, useState } from "react";
import heroBg from "../images/Services/project2.jpg";
import aboutImg from "../images/Services/ourteam.jpg";
import processImg from "../images/Services/project4.jpg";
import testimonialsImg from "../images/Services/project5.jpg";
import aboutUsImage from "../images/Services/ourteam.jpg"; // <-- Add your image here
import "./Home.css";
import ModularKitchen from "../images/Services/modularkitchen.jpg";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../images/Services/Tvunits.jpg";
import Wardrobe from "../images/Services/wardrobs.jpg";
import OfficeSpace from "../images/Services/officespaces.jpg";
import Bedroom from "../images/Services/bedroom.jpg";
import ExternalArchitecture from "../images/Services/ExternalArc.jpg";
import { Link } from "react-router-dom";

const homeCards = [
  {
    title: "Building Your Dreams, Brick by Brick",
    img: heroBg,
    desc: "Gajulapalli Infra and Constructions - Trusted for Quality, Timely Delivery, and Innovation.",
    cta: true,
  },
  
  {
    title: "Our Process",
    img: processImg,
    desc: "Consultation, Planning, Execution, and Delivery. We ensure quality and efficiency at every step.",
  },
];

const homeServices = [
  {
    title: "Residential Interior Design",
    description:
      "We design beautiful, functional homes â€” from compact apartments to spacious villas â€” tailored to your lifestyle and taste.",
    image: Bedroom,
  },
  {
    title: "Commercial Interior Design",
    description:
      "Smart, modern workspaces that enhance productivity and reflect your brand â€” ideal for offices, studios, and retail spaces.",
    image: OfficeSpace,
  },
  {
    title: "Modular Kitchen Design",
    description:
      "Relaxing, personalized sanctuaries that combine comfort, storage, and aesthetic harmony.",
    image: ModularKitchen,
  },
  {
    title: "False Ceiling Design",
    description:
      "Stylish and functional false ceilings that enhance lighting, acoustics, and the overall aesthetic of any room.",
    image: FalseCeiling,
  },
];

function Home() {
  const cardRefs = useRef([]);
  const [visibleCards, setVisibleCards] = useState([]);

  useEffect(() => {
    setVisibleCards(Array(homeCards.length).fill(false));
    const observer = new window.IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const idx = Number(entry.target.getAttribute("data-index"));
          if (entry.isIntersecting) {
            setVisibleCards((prev) => {
              if (!prev[idx]) {
                const updated = [...prev];
                updated[idx] = true;
                return updated;
              }
              return prev;
            });
          }
        });
      },
      { threshold: 0.2 }
    );
    cardRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });
    return () => observer.disconnect();
  }, []);

  return (
    <div className="home-root">
      {/* ========== ABOUT US HERO SECTION ========== */}
      <div className="home-aboutus-container">
        <div className="aboutus-content">
          <div className="aboutus-left">
            <div className="aboutus-heading">
              <div className="aboutus-highlight-bar"></div>
              <h2>Welcome to <br />GAJULAPALLI INFRA and CONSTRUCTIONS</h2>
            </div>
            <p className="aboutus-subheading">
              Your Vision. Our Expertise. <br /> Perfect Interiors & Construction.
            </p>
            <p>
              Welcome to <strong>GAJULAPALLI INFRA and CONSTRUCTIONS</strong>, your one-stop
              destination for complete construction solutions. We specialize in
              residential and commercial projects including <strong>architectural planning, 3D elevation,
              interior design, government approvals,</strong> and <strong>turnkey execution</strong>.
            </p>
            <p>We ensure full transparency and quality through:</p>
            <ul className="aboutus-list">
              <li>âœ… Smart planning and accurate budgeting</li>
              <li>âœ… Weekly site progress reports with visuals</li>
              <li>âœ… Vastu-compliant and smart home-ready designs</li>
              <li>âœ… Customized interiors and high-end finishes</li>
              <li>âœ… On-time delivery with quality you can trust</li>
            </ul>
            <p>
              Whether you're building your dream home or a modern commercial space,
              <strong> GAJULAPALLI INFRA and CONSTRUCTIONS</strong> is your trusted partner from
              start to finish.
            </p>
            <p className="aboutus-cta">ðŸ“ž Let's Build It Right â€” Contact us today!</p>
          </div>
          <div className="aboutus-right">
            <img src={aboutUsImage} alt="About Us" className="aboutus-image" />
          </div>
        </div>
      </div>
      {/* Services Hero Section */}
      <section className="home-services-hero">
        <div className="home-services-hero-left">
          <div className="home-services-hero-label">OUR SERVICE</div>
          <h1 className="home-services-hero-title">Solutions for every corner</h1>
          <p className="home-services-hero-desc">
            We provide end-to-end interior design services, including space planning, modular kitchens, wardrobes, false ceilings, lighting, and custom furniture. Whether it's your home or workspace, we create interiors that are stylish, functional, and uniquely yours.
          </p>
          <Link to="/services" className="home-services-hero-btn">Discover More</Link>
        </div>
        <div className="home-services-hero-right">
          <div className="home-services-hero-grid">
            {homeServices.map((service, idx) => (
              <div className="home-services-card" key={idx}>
                <h3 className="home-services-card-title">{service.title}</h3>
                <p className="home-services-card-desc">{service.description}</p>
                <button className="home-services-card-btn">View Details â†’</button>
              </div>
            ))}
          </div>
        </div>
      </section>
      <div className="home-bg" />
