import React from "react";
import ServiceCard from "../components/ServiceCard";

const services = [
  {
    
    title: "Residential Construction",
    description: "Custom homes, apartments, and villas built to your specifications.",
  },
  {
    
    title: "Commercial Construction",
    description: "Office buildings, retail spaces, and commercial complexes.",
  },
  {
    
    title: "Industrial Projects",
    description: "Factories, warehouses, and large-scale industrial facilities.",
  },
  {icon: <svg className="w-10 h-10 mx-auto" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M12 20v-6m0 0V4m0 10H6m6 0h6" /></svg>,
    title: "Renovation & Remodeling",
    description: "Upgrades, expansions, and modernizations for existing structures.",
  },
  {
    icon: <svg className="w-10 h-10 mx-auto" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24"><path d="M8 17l4-4 4 4m0 0V7" /></svg>,
    title: "Project Management",
    description: "End-to-end project management for seamless execution.",
  },
];

function Services() {
  return (
    <div className="max-w-6xl mx-auto py-12 px-4">
      <h1 className="text-3xl font-bold mb-8 text-blue-700 text-center">Our Services</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {services.map((service, idx) => (
          <ServiceCard key={idx} icon={service.icon} title={service.title} description={service.description} />
        ))}
      </div>
    </div>
  );
}

export default Services;
