import React from "react";
import "./Services.css";

// Importing images
import ModularKitchen from "../images/Services/modularkitchen.jpg";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../imag";
import Wardrobe from "../assets/services/wardrobe.jpg";
import OfficeSpace from "../assets/services/office-space.jpg";
import Bedroom from "../assets/services/bedroom.jpg";
import ExternalArchitecture from "../assets/services/external-architecture.jpg";

const services = [
  { title: "Modular Kitchen", image: ModularKitchen },
  { title: "False Ceiling", image: FalseCeiling },
  { title: "TV Units", image: TVUnit },
  { title: "Wardrobes", image: Wardrobe },
  { title: "Office Spaces", image: OfficeSpace },
  { title: "Bedrooms", image: Bedroom },
  { title: "External Architecture", image: ExternalArchitecture },
];

function Services() {
  return (
    <section className="services-section">
      <div className="services-container">
        <h2 className="services-heading">Our Services</h2>
        <div className="services-grid">
          {services.map((service, index) => (
            <div key={index} className="service-card">
              <img
                src={service.image}
                alt={service.title}
                className="service-image"
              />
              <div className="service-content">
                <h3 className="service-title">{service.title}</h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Services;
