import React, { useState } from "react";
import { Link } from "react-router-dom";

function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);

  const handleMenuToggle = () => setMenuOpen(!menuOpen);

  return (
    <nav className="bg-white border-b shadow-sm sticky top-0 z-50">
      <div className="container mx-auto px-4 py-3 flex items-center justify-between">
        {/* Logo/Brand */}
        <div className="flex items-center space-x-2">
          <span className="font-bold text-xl text-blue-700">Gajulapalli Infra</span>
        </div>
        {/* Desktop Links */}
        <div className="hidden md:flex space-x-8">
          <Link to="/" className="hover:text-blue-600 font-medium">Home</Link>
          <Link to="/about" className="hover:text-blue-600 font-medium">About</Link>
          <Link to="/services" className="hover:text-blue-600 font-medium">Services</Link>
          <Link to="/projects" className="hover:text-blue-600 font-medium">Projects</Link>
          <Link to="/contact" className="hover:text-blue-600 font-medium">Contact</Link>
        </div>
        {/* Hamburger Menu Button */}
        <button
          className="md:hidden focus:outline-none"
          aria-label="Toggle menu"
          onClick={handleMenuToggle}
        >
          <svg className="w-8 h-8" fill="none" stroke="currentColor" strokeWidth="2" viewBox="0 0 24 24">
            <path d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      </div>
      {/* Mobile Menu */}
      {menuOpen && (
        <div className="md:hidden bg-white border-t px-4 pb-4">
          <Link to="/" className="block py-2 hover:text-blue-600 font-medium" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/about" className="block py-2 hover:text-blue-600 font-medium" onClick={() => setMenuOpen(false)}>About</Link>
          <Link to="/services" className="block py-2 hover:text-blue-600 font-medium" onClick={() => setMenuOpen(false)}>Services</Link>
          <Link to="/projects" className="block py-2 hover:text-blue-600 font-medium" onClick={() => setMenuOpen(false)}>Projects</Link>
          <Link to="/contact" className="block py-2 hover:text-blue-600 font-medium" onClick={() => setMenuOpen(false)}>Contact</Link>
        </div>
      )}
    </nav>
  );
}

export default Navbar;
