import React from "react";

import ModularKitchen from "../images/Services/modularkitchen.jpg";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../images/Services/Tvunits.jpg";
import Wardrobe from "../images/Services/wardrobs.jpg";
import OfficeSpace from "../images/Services/officespaces.jpg";
import Bedroom from "../images/Services/bedroom.jpg";
import ExternalArchitecture from "../images/Services/ExternalArc.jpg";

const services = [
  {
    title: "Modular Kitchen",
    image: ModularKitchen,
    description: "Modern modular kitchens with smart storage and sleek design.",
  },
  {
    title: "False Ceiling",
    image: FalseCeiling,
    description: "Add elegance and insulation with custom ceiling finishes.",
  },
  {
    title: "TV Units",
    image: TVUnit,
    description: "Stylish and space-efficient entertainment units.",
  },
  {
    title: "Wardrobes",
    image: Wardrobe,
    description: "Tailored wardrobes for every room and requirement.",
  },
  {
    title: "Office Spaces",
    image: OfficeSpace,
    description: "Elegant and productive workspace interior solutions.",
  },
  {
    title: "Bedrooms",
    image: Bedroom,
    description: "Tranquil bedroom setups designed for comfort and class.",
  },
  {
    title: "External Architecture",
    image: ExternalArchitecture,
    description: "Stunning facades and architectural exterior design.",
  },
  // Fill up to 12 if needed (for full 4×3)
];

function Services() {
  return (
    <section className="bg-slate-50 dark:bg-slate-900 py-12 px-4 sm:px-6 lg:px-10">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-blue-700 dark:text-blue-400 mb-12">
          Our Services
        </h2>

        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white dark:bg-slate-800 rounded-xl shadow hover:shadow-lg transition duration-300 overflow-hidden"
            >
              <div className="w-full h-52">
                <img
                  src={service.image}
                  alt={service.title}
                  className="w-full h-full object-cover object-center"
                />
              </div>
              <div className="p-4 text-center">
                <h3 className="text-lg font-semibold text-slate-800 dark:text-slate-100">
                  {service.title}
                </h3>
                <p className="text-sm text-gray-600 dark:text-gray-300 mt-2">
                  {service.description}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Services;
