import React, { useState } from "react";

function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  return (
    <div className="max-w-2xl mx-auto py-12 px-4">
      <h1 className="text-3xl font-bold mb-8 text-blue-700 text-center">Contact Us</h1>
      {submitted ? (
        <div className="bg-green-100 text-green-700 p-4 rounded text-center mb-8">Thank you for reaching out! We will get back to you soon.</div>
      ) : (
        <form onSubmit={handleSubmit} className="bg-white shadow-md rounded-lg p-8 mb-8">
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="name">Name</label>
            <input type="text" id="name" name="name" value={form.name} onChange={handleChange} required className="w-full border rounded px-3 py-2" />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="email">Email</label>
            <input type="email" id="email" name="email" value={form.email} onChange={handleChange} required className="w-full border rounded px-3 py-2" />
          </div>
          <div className="mb-4">
            <label className="block text-gray-700 mb-2" htmlFor="message">Message</label>
            <textarea id="message" name="message" value={form.message} onChange={handleChange} required className="w-full border rounded px-3 py-2" rows="5" />
          </div>
          <button type="submit" className="bg-blue-700 text-white px-6 py-2 rounded font-semibold hover:bg-blue-800">Send Message</button>
        </form>
      )}
      <div className="text-center text-gray-700">
        <div className="mb-2 font-semibold">Gajulapalli Infra and Constructions</div>
        <div>123 Main Street, Hyderabad, India</div>
        <div>Phone: +91 98765 43210</div>
        <div>Email: info@gajulapalliinfra.com</div>
      </div>
    </div>
  );
}

export default Contact;
