import React, { useState } from "react";
import "./Contact.css";

function Contact() {
  const [form, setForm] = useState({ name: "", email: "", message: "" });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  return (
    <div className="contact-root">
      <h1 className="contact-title">Contact Us</h1>
      <div className="contact-container">
        <div className="contact-card">
          {submitted ? (
            <div className="contact-success">Thank you for reaching out! We will get back to you soon.</div>
          ) : (
            <form onSubmit={handleSubmit} className="contact-form">
              <div>
                <label className="contact-label" htmlFor="name">Name</label>
                <input type="text" id="name" name="name" value={form.name} onChange={handleChange} required className="contact-input" />
              </div>
              <div>
                <label className="contact-label" htmlFor="email">Email</label>
                <input type="email" id="email" name="email" value={form.email} onChange={handleChange} required className="contact-input" />
              </div>
              <div>
                <label className="contact-label" htmlFor="message">Message</label>
                <textarea id="message" name="message" value={form.message} onChange={handleChange} required className="contact-textarea" rows="5" />
              </div>
              <button type="submit" className="contact-button">Send Message</button>
            </form>
          )}
        </div>
        <div className="contact-map-container">
          <iframe
            title="Company Location"
            src="https://www.google.com/maps?q=Google+India,+Hyderabad&output=embed"
            className="contact-map"
            allowFullScreen=""
            loading="lazy"
            referrerPolicy="no-referrer-when-downgrade"
          ></iframe>
        </div>
      </div>
    </div>
  );
}

export default Contact;
