import React, { useRef, useEffect, useState } from "react";
import heroBg from "../images/Services/project2.jpg";
import aboutImg from "../images/Services/ourteam.jpg";
import processImg from "../images/Services/project4.jpg";
import testimonialsImg from "../images/Services/project5.jpg";
import "./Home.css";

const homeCards = [
  {
    title: "Building Your Dreams, Brick by Brick",
    img: heroBg,
    desc: "Gajulapalli Infra and Constructions - Trusted for Quality, Timely Delivery, and Innovation.",
    cta: true,
  },
  {
    title: "About Us",
    img: aboutImg,
    desc: "With decades of experience, we deliver residential, commercial, and industrial projects with a focus on quality and customer satisfaction.",
  },
  {
    title: "Our Process",
    img: processImg,
    desc: "Consultation, Planning, Execution, and Delivery. We ensure quality and efficiency at every step.",
  },
  {
    title: "Testimonials",
    img: testimonialsImg,
    desc: "Our clients love our professionalism, reliability, and quality. Read what they have to say about us!",
  },
];

function Home() {
  const cardRefs = useRef([]);
  const [visibleCards, setVisibleCards] = useState([]);

  useEffect(() => {
    setVisibleCards(Array(homeCards.length).fill(false));
    const observer = new window.IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const idx = Number(entry.target.getAttribute('data-index'));
          if (entry.isIntersecting) {
            setVisibleCards((prev) => {
              if (!prev[idx]) {
                const updated = [...prev];
                updated[idx] = true;
                return updated;
              }
              return prev;
            });
          }
        });
      },
      { threshold: 0.2 }
    );
    cardRefs.current.forEach((ref) => {
      if (ref) observer.observe(ref);
    });
    return () => observer.disconnect();
  }, []);

  return (
    <div className="home-root">
      <div className="home-bg" />
      <div className="home-overlay" />
      <div className="home-content">
        {/* Home Section Cards Grid */}
        <div className="home-section-cards-grid">
          {homeCards.map((card, idx) => {
            // 1st and 3rd: image right, text left, white bg; 2nd and 4th: image left, text right, transparent bg
            const isImageRight = idx % 2 === 0;
            const isTransparent = idx % 2 === 1;
            const cardClass = `home-square-card ${isImageRight ? 'even' : 'odd'}${isTransparent ? ' transparent-bg' : ''} ${visibleCards[idx] ? 'card-visible' : 'card-hidden'}`;
            return (
              <div
                className={cardClass}
                key={idx}
                ref={el => cardRefs.current[idx] = el}
                data-index={idx}
              >
                {isImageRight ? (
                  <>
                    <div className="home-square-card-content">
                      <div className="home-square-card-title">{card.title}</div>
                      <div className="home-square-card-desc">{card.desc}</div>
                      {card.cta && (
                        <a href="/contact">
                          <button className="get-in-touch-btn">Get in Touch</button>
                        </a>
                      )}
                    </div>
                    <img src={card.img} alt={card.title} className="home-square-card-img" />
                  </>
                ) : (
                  <>
                    <img src={card.img} alt={card.title} className="home-square-card-img" />
                    <div className="home-square-card-content">
                      <div className="home-square-card-title">{card.title}</div>
                      <div className="home-square-card-desc">{card.desc}</div>
                      {card.cta && (
                        <a href="/contact">
                          <button className="get-in-touch-btn">Get in Touch</button>
                        </a>
                      )}
                    </div>
                  </>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
}

export default Home;
