import React from "react";
import StepCard from "../components/StepCard";
import TestimonialCard from "../components/TestimonialCard";
import heroBg from "../images/Services/project2.jpg";
import "./Home.css";

const steps = [
  { step: 1, title: "Consultation", description: "We discuss your needs and vision for the project." },
  { step: 2, title: "Planning", description: "Our experts design and plan every detail." },
  { step: 3, title: "Execution", description: "We build with quality and efficiency." },
  { step: 4, title: "Delivery", description: "On-time handover and post-project support." },
];

const testimonials = [
  { name: "Ravi Kumar", role: "Client", testimonial: "Professional and reliable team! Our dream home was delivered on time." },
  { name: "Sita Devi", role: "Client", testimonial: "Excellent service and quality construction. Highly recommended!" },
];

function Home() {
  return (
    <div className="home-root">
      <div className="home-bg" />
      <div className="home-overlay" />
      <div className="home-content">
        {/* Hero Section */}
        <section className="home-hero-section">
          <h1 className="text-4xl font-bold mb-4">Building Your Dreams, Brick by Brick</h1>
          <p className="text-lg mb-6">Gajulapalli Infra and Constructions - Trusted for Quality, Timely Delivery, and Innovation.</p>
          <a href="/contact" className="bg-white text-blue-700 px-6 py-2 rounded font-semibold shadow hover:bg-blue-50">Get in Touch</a>
        </section>

        {/* About Brief */}
        <section className="home-about-section py-12 px-4 max-w-3xl mx-auto text-center">
          <h2 className="text-2xl font-bold mb-2">About Us</h2>
          <p className="text-gray-700">With decades of experience, we deliver residential, commercial, and industrial projects with a focus on quality and customer satisfaction.</p>
        </section>

        {/* Steps/Process */}
        <section className="home-process-section py-12 bg-gray-50 bg-opacity-80">
          <h2 className="text-2xl font-bold text-center mb-8">Our Process</h2>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6 max-w-5xl mx-auto">
            {steps.map((s) => (
              <StepCard key={s.step} step={s.step} title={s.title} description={s.description} />
            ))}
          </div>
        </section>

        {/* Testimonials */}
        <section className="home-testimonials-section py-12 px-4 max-w-5xl mx-auto">
          <h2 className="text-2xl font-bold text-center mb-8">What Our Clients Say</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {testimonials.map((t, idx) => (
              <TestimonialCard key={idx} name={t.name} role={t.role} testimonial={t.testimonial} />
            ))}
          </div>
        </section>
      </div>
    </div>
  );
}

export default Home;
