import React, { useRef, useEffect, useState } from "react";
import "./ServiceCard.css";

// Importing images
import ModularKitchen from "../images/Services/modularkitchen.jpg";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../images/Services/Tvunits.jpg";
import Wardrobe from "../images/Services/wardrobs.jpg";
import OfficeSpace from "../images/Services/officespaces.jpg";
import Bedroom from "../images/Services/bedroom.jpg";
import ExternalArchitecture from "../images/Services/ExternalArc.jpg";

function ServiceCard({ service, index, title, description, image }) {
  const rowRef = useRef(null);
  const [inView, setInView] = useState(false);

  useEffect(() => {
    const observer = new window.IntersectionObserver(
      ([entry]) => {
        setInView(entry.isIntersecting);
      },
      { threshold: 0.2 }
    );
    if (rowRef.current) {
      observer.observe(rowRef.current);
    }
    return () => observer.disconnect();
  }, []);

  // Support both prop styles: either pass service object or individual props
  const cardTitle = title || (service && service.title);
  const cardDescription = description || (service && service.description);
  const cardImage = image || (service && service.image);

  return (
    <div
      ref={rowRef}
      className={`service-row ${index % 2 === 1 ? "reverse" : ""} ${inView ? "in-view" : ""}`}
    >
      <div className="service-row-content">
        <div className="service-image-half">
          {cardImage && (
            <img
              src={cardImage}
              alt={cardTitle}
              className="service-image"
            />
          )}
        </div>
        <div className="service-content-half">
          <h3 className="service-title">{cardTitle}</h3>
          <p className="service-description">{cardDescription}</p>
          <a href="/contact">
            <button className="get-in-touch-btn">Get in Touch</button>
          </a>
        </div>
      </div>
    </div>
  );
}

export default ServiceCard;
