import React from "react";
import ServiceCard from "../components/ServiceCard";

import ModularKitchen from "../images/Services/modularkitchen.jpg";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../images/Services/Tvunits.jpg";
import Wardrobe from "../images/Services/wardrobs.jpg";
import OfficeSpace from "../images/Services/officespaces.jpg";
import Bedroom from "../images/Services/bedroom.jpg";
import ExternalArchitecture from "../images/Services/ExternalArc.jpg";


{services.map((service, idx) => (
  <div id={service.title.toLowerCase().replace(/\s+/g, "-")} key={idx}>
    <ServiceCard
      title={service.title}
      description={service.description}
      image={service.image}
      index={idx}
    />
  </div>
))}


const services = [
  {
    title: "Residential Construction",
    description: "Custom homes, apartments, and villas built to your specifications.",
    image: Bedroom,
  },
  {
    title: "Commercial Construction",
    description: "Office buildings, retail spaces, and commercial complexes.",
    image: OfficeSpace,
  },
  {
    title: "Industrial Projects",
    description: "Factories, warehouses, and large-scale industrial facilities.",
    image: ExternalArchitecture,
  },
  {
    title: "Renovation & Remodeling",
    description: "Upgrades, expansions, and modernizations for existing structures.",
    image: Wardrobe,
  },
  {
    title: "Project Management",
    description: "End-to-end project management for seamless execution.",
    image: TVUnit,
  },
  {
    title: "Modular Kitchen",
    description: "Custom-designed modular kitchens with modern fittings and finishes for efficient and stylish cooking spaces.",
    image: ModularKitchen,
  },
  {
    title: "False Ceiling",
    description: "Modern false ceiling designs for enhanced aesthetics and lighting.",
    image: FalseCeiling,
  },
  {
    title: "TV Units",
    description: "Elegant and functional TV units tailored to your living room aesthetics and storage needs.",
    image: TVUnit,
  },
  {
    title: "Wardrobes",
    description: "Spacious and customizable wardrobes to organize your clothing and accessories beautifully.",
    image: Wardrobe,
  },
  {
    title: "Office Spaces",
    description: "Modern office interiors designed for productivity, comfort, and a professional look.",
    image: OfficeSpace,
  },
  {
    title: "Bedrooms",
    description: "Cozy and stylish bedroom designs for restful nights and refreshing mornings.",
    image: Bedroom,
  },
  {
    title: "External Architecture",
    description: "Striking external architectural solutions to enhance your building's curb appeal and value.",
    image: ExternalArchitecture,
  },
];

function Services() {
  return (
    <div className="max-w-6xl mx-auto py-12 px-4">
      <h1 className="text-3xl font-bold mb-8 text-blue-700 text-center">Our Services</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
        {services.map((service, idx) => (
          <ServiceCard key={idx} title={service.title} description={service.description} image={service.image} index={idx} />
        ))}
      </div>
    </div>
  );
}

export default Services;
