import React from "react";

import ModularKitchen from "../images/Services/modularkitchen.jpg";
import FalseCeiling from "../images/Services/falseCeiling.jpg";
import TVUnit from "../images/Services/Tvunits.jpg";
import Wardrobe from "../images/Services/wardrobs.jpg";
import OfficeSpace from "../images/Services/officespaces.jpg";
import Bedroom from "../images/Services/bedroom.jpg";
import ExternalArchitecture from "../images/Services/ExternalArc.jpg";

const services = [
  { title: "Modular Kitchen", image: ModularKitchen },
  { title: "False Ceiling", image: FalseCeiling },
  { title: "TV Units", image: TVUnit },
  { title: "Wardrobes", image: Wardrobe },
  { title: "Office Spaces", image: OfficeSpace },
  { title: "Bedrooms", image: Bedroom },
  { title: "External Architecture", image: ExternalArchitecture },
];

function Services() {
  return (
    <section className="bg-slate-50 dark:bg-slate-900 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold text-center text-blue-700 dark:text-blue-400 mb-10">
          Our Services
        </h2>
        <div className="grid grid-cols-1 sm:grid-cols-2 gap-8">
          {services.map((service, index) => (
            <div
              key={index}
              className="bg-white dark:bg-slate-800 rounded-xl overflow-hidden shadow-md hover:shadow-xl transition-all duration-300"
            >
              <img
                src={service.image}
                alt={service.title}
                className="w-full h-52 object-cover transform hover:scale-105 transition-transform duration-300"
              />
              <div className="p-5 text-center">
                <h3 className="text-xl font-semibold text-slate-800 dark:text-slate-100">
                  {service.title}
                </h3>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Services;
