import React, { useState } from "react";
import "./Contact.css";

function Contact() {
  const [form, setForm] = useState({ name: "", email: "", phone: "", message: "" });
  const [submitted, setSubmitted] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
  };

  return (
    <div className="contact-root">
      <div className="contact-card">
        <h1 className="contact-title">Contact Us</h1>
        {submitted ? (
          <div className="contact-success">Thank you for reaching out! We will get back to you soon.</div>
        ) : (
          <form onSubmit={handleSubmit} className="contact-form">
            <div>
              <label className="contact-label" htmlFor="name">Name</label>
              <input type="text" id="name" name="name" value={form.name} onChange={handleChange} required className="contact-input" />
            </div>
            <div>
              <label className="contact-label" htmlFor="email">Email</label>
              <input type="email" id="email" name="email" value={form.email} onChange={handleChange} required className="contact-input" />
            </div>
            <div>
              <label className="contact-label" htmlFor="phone">Phone</label>
              <input type="tel" id="phone" name="phone" value={form.phone} onChange={handleChange} required className="contact-input" pattern="[0-9+\-() ]{7,}" maxLength="20" />
            </div>
            <div>
              <label className="contact-label" htmlFor="message">Message</label>
              <textarea id="message" name="message" value={form.message} onChange={handleChange} required className="contact-textarea" rows="5" />
            </div>
            <button type="submit" className="contact-button">Send Message</button>
          </form>
        )}
      </div>
      <div className="contact-info contact-info-grid">
        <div className="contact-info-col">
          <div className="contact-info-title">Gajulapalli Infra and Constructions</div>
          <div>123 Main Street, Hyderabad, India</div>
        </div>
        <div className="contact-info-col">
          <div className="contact-info-title">Phone</div>
          <div>+91 98765 43210</div>
        </div>
        <div className="contact-info-col">
          <div className="contact-info-title">Email</div>
          <div>info@gajulapalliinfra.com</div>
        </div>
      </div>
    </div>
  );
}

export default Contact;
