// NavBar.jsx
import React, { useState } from "react";
import { Link } from "react-router-dom";
import Services from "../"; // <-- Import the Services component
import "./Navbar.css";

function Navbar() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [showServices, setShowServices] = useState(false); // <-- Toggle for services

  const handleMenuToggle = () => setMenuOpen(!menuOpen);
  const handleServicesToggle = () => setShowServices(!showServices); // <-- Toggle handler

  return (
    <nav className="custom-navbar">
      <div className="nav-container">
        <div className="brand">Gajulapalli Infra</div>

        {/* Desktop Nav */}
        <div className="nav-links">
          <Link to="/" className="nav-item">Home</Link>
          <Link to="/about" className="nav-item">About</Link>
          <span className="nav-item" onClick={handleServicesToggle}>Services</span>
          <Link to="/projects" className="nav-item">Projects</Link>
          <Link to="/contact" className="nav-item">Contact</Link>
        </div>

        {/* Hamburger */}
        <button className="hamburger" onClick={handleMenuToggle}>
          <svg viewBox="0 0 24 24" stroke="currentColor" fill="none" strokeWidth="2" className="hamburger-icon">
            <path d="M4 6h16M4 12h16M4 18h16" />
          </svg>
        </button>
      </div>

      {/* Mobile Menu */}
      {menuOpen && (
        <div className="mobile-menu">
          <Link to="/" className="mobile-item" onClick={() => setMenuOpen(false)}>Home</Link>
          <Link to="/about" className="mobile-item" onClick={() => setMenuOpen(false)}>About</Link>
          <span className="mobile-item" onClick={() => { handleServicesToggle(); setMenuOpen(false); }}>Services</span>
          <Link to="/projects" className="mobile-item" onClick={() => setMenuOpen(false)}>Projects</Link>
          <Link to="/contact" className="mobile-item" onClick={() => setMenuOpen(false)}>Contact</Link>
        </div>
      )}

      {/* Conditionally render services section below nav */}
      {showServices && (
        <div className="services-dropdown">
          <Services />
        </div>
      )}
    </nav>
  );
}

export default Navbar;
